CREATE TABLE food (id INT AUTO_INCREMENT, food_name VARCHAR(50), typical_value DECIMAL (5,2) unsigned, units VARCHAR(50) unsigned,
                    carbs DECIMAL(5,2) unsigned, fat DECIMAL(5,2) unsigned , protein DECIMAL(5,2) unsigned, salt DECIMAL(5,2) unsigned,
                    sugar DECIMAL(5,2) unsigned, username VARCHAR(50), PRIMARY KEY(id));